parts.shutter(
    initializationStatus := statuses.initializationStatus,
    operatorStatus := operatorStatus,
    operatingStatus := statuses.operatingStatus,
    config := config.shutter);
parts.rotation(
    initializationStatus := statuses.initializationStatus,
    operatorStatus := operatorStatus,
    operatingStatus := statuses.operatingStatus,
    config := config.rotation);
parts.io();
parts.configManager( isEnabled := operatorStatus.tech );
statuses.initializationStatus();
statuses.healthStatus(
    isGood := parts.shutter.statuses.healthStatus.isGood AND (parts.rotation.statuses.healthStatus.isGood AND parts.io.statuses.healthStatus.isGood),
    hasWarning := parts.shutter.statuses.healthStatus.hasWarning OR (parts.rotation.statuses.healthStatus.hasWarning OR parts.io.statuses.healthStatus.hasWarning));
statuses.busyStatus( isBusy := parts.shutter.statuses.busyStatus.busy OR parts.rotation.statuses.busyStatus.busy );
statuses.operatingStatus( superState := statuses.initializationStatus.initialized );
statuses.poweredStatus( isEnabled := parts.rotation.statuses.poweredStatus.enabled );
processes.initialize( isEnabled := statuses.initializationStatus.shutdown OR (statuses.initializationStatus.initializingFailed OR statuses.initializationStatus.initialized) );
processes.lock( isEnabled := operatorStatus.tech AND statuses.initializationStatus.initialized );
processes.unlock( isEnabled := operatorStatus.tech AND statuses.initializationStatus.locked );
processes.changeOperatingState( isEnabled := statuses.busyStatus.idle AND statuses.initializationStatus.initialized );
processes.reset( isEnabled := statuses.busyStatus.idle AND statuses.initializationStatus.initialized );
processes.powerOn( isEnabled := statuses.initializationStatus.initialized AND processes.powerOn.statuses.busyStatus.idle );
processes.powerOff( isEnabled := statuses.initializationStatus.initialized AND processes.powerOff.statuses.busyStatus.idle );
processes.syncWithAxes();
processes.trackAxes( isEnabled := statuses.poweredStatus.enabled );
processes.stop( isEnabled := statuses.busyStatus.busy OR isTracking );

parts.pumpsRelay( isEnabled := operatorStatus.tech );
parts.upperOpenRelay( isEnabled := operatorStatus.tech );
parts.upperCloseRelay( isEnabled := operatorStatus.tech );
parts.lowerOpenRelay( isEnabled := operatorStatus.tech );
parts.lowerCloseRelay( isEnabled := operatorStatus.tech );
statuses.apertureStatus(
    superState := NOT(wirelessTimeout OR wirelessError),
    isOpen := statuses.lowerApertureStatus.open AND statuses.upperApertureStatus.open,
    isClosed := statuses.lowerApertureStatus.closed AND statuses.upperApertureStatus.closed);
statuses.lowerApertureStatus(
    superState := NOT(wirelessTimeout OR wirelessError),
    isOpen := lowerOpenSignal,
    isClosed := lowerClosedSignal);
statuses.upperApertureStatus(
    superState := NOT(wirelessTimeout OR wirelessError),
    isOpen := upperOpenSignal,
    isClosed := upperClosedSignal);
statuses.healthStatus(
    isGood := (NOT(wirelessTimeout OR wirelessError)) AND (processes.reset.statuses.healthStatus.isGood AND (processes.open.statuses.healthStatus.isGood AND (processes.close.statuses.healthStatus.isGood AND (processes.stop.statuses.healthStatus.isGood AND (processes.lowerOpen.statuses.healthStatus.isGood AND (processes.lowerClose.statuses.healthStatus.isGood AND (processes.upperOpen.statuses.healthStatus.isGood AND processes.upperClose.statuses.healthStatus.isGood))))))),
    hasWarning := processes.reset.statuses.healthStatus.hasWarning OR (processes.open.statuses.healthStatus.hasWarning OR (processes.close.statuses.healthStatus.hasWarning OR (processes.stop.statuses.healthStatus.hasWarning OR (processes.lowerOpen.statuses.healthStatus.hasWarning OR (processes.lowerClose.statuses.healthStatus.hasWarning OR (processes.upperOpen.statuses.healthStatus.hasWarning OR processes.upperClose.statuses.healthStatus.hasWarning)))))));
statuses.busyStatus( isBusy := processes.reset.statuses.busyStatus.busy OR (processes.open.statuses.busyStatus.busy OR (processes.close.statuses.busyStatus.busy OR (processes.stop.statuses.busyStatus.busy OR (processes.lowerOpen.statuses.busyStatus.busy OR (processes.lowerClose.statuses.busyStatus.busy OR (processes.upperOpen.statuses.busyStatus.busy OR processes.upperClose.statuses.busyStatus.busy)))))) );
processes.reset( isEnabled := TRUE );
processes.open( isEnabled := statuses.busyStatus.idle AND initializationStatus.initialized );
processes.close( isEnabled := statuses.busyStatus.idle AND initializationStatus.initialized );
processes.stop( isEnabled := statuses.busyStatus.busy );
processes.lowerOpen( isEnabled := processes.open.isEnabled );
processes.lowerClose( isEnabled := processes.close.isEnabled );
processes.upperOpen( isEnabled := processes.close.isEnabled );
processes.upperClose();

parts.masterAxis( isEnabled := operatorStatus.tech );
parts.slaveAxis( isEnabled := operatorStatus.tech );
parts.drive( isEnabled := operatorStatus.tech );
statuses.healthStatus(
    isGood := (parts.masterAxis.statuses.healthStatus.isGood AND (parts.slaveAxis.statuses.healthStatus.isGood AND (parts.drive.statuses.healthStatus.isGood AND (processes.reset.statuses.healthStatus.isGood AND processes.stop.statuses.healthStatus.isGood)))) AND (NOT(masterSlaveLagError)),
    hasWarning := parts.masterAxis.statuses.healthStatus.hasWarning OR (parts.slaveAxis.statuses.healthStatus.hasWarning OR (parts.drive.statuses.healthStatus.hasWarning OR (processes.reset.statuses.healthStatus.hasWarning OR processes.stop.statuses.healthStatus.hasWarning))));
statuses.busyStatus( isBusy := parts.masterAxis.statuses.busyStatus.busy OR (parts.slaveAxis.statuses.busyStatus.busy OR (parts.drive.statuses.busyStatus.busy OR (processes.reset.statuses.busyStatus.busy OR processes.stop.statuses.busyStatus.busy))) );
statuses.poweredStatus( isEnabled := parts.masterAxis.statuses.poweredStatus.enabled AND parts.slaveAxis.statuses.poweredStatus.enabled );
processes.reset( isEnabled := statuses.busyStatus.idle );
processes.stop( isEnabled := statuses.busyStatus.busy );

